mapper.insert:
insert into noback_member(
	employee_num
	, employee_name
	, employee_ssn
	, employee_address
	, employee_dep
	, employee_pos
	, employee_email
	, employee_level
	, employee_phone
	, employee_picture
)
values (
	#{employee_num}
	, #{employee_name}
	, #{employee_ssn}
	, #{employee_address}
	, #{employee_dep}
	, #{employee_pos}
	, #{employee_email}
	, #{employee_level}
	, #{employee_phone}
	, #{employee_picture}
)

mapper.update:
update
	noback_member
set
	employee_name = #{employee_name}
	, employee_address = #{employee_address}
	, employee_dep = #{employee_dep}
	, employee_pos = #{employee_pos}
	, employee_email = #{employee_email}

	, employee_level = #{employee_level}

	, employee_phone = #{employee_phone}

	, employee_picture = #{employee_picture}

where	
	employee_num = #{employee_num}

update noback_alarm
set schedule = #{schedule}

	where employee_num = #{employee_num}

update noback_alarm
set messenger = #{messenger}
where employee_num = #{employee_num}

update noback_alarm
set sign = #{sign}
where employee_num = #{employee_num}
