mapper.insert:
insert into noback_schedule ( schedule_num, schedule_title, schedule_writer, schedule_level,
schedule_memo,
schedule_start, schedule_end,
schedule_allDay
) values ( cal_seq.nextval, #{schedule_title}, #{schedule_writer}, #{schedule_level},
#{schedule_memo},
TO_DATE(#{schedule_start}, 'yyyy-MM-dd HH24:MI:SS'), TO_DATE(#{schedule_end}, 'yyyy-MM-dd HH24:MI:SS'),
#{schedule_allDay}
)

mapper.update:
update 
	noback_schedule
set 
	schedule_title = #{schedule_title}
	, schedule_start = #{schedule_start}
	, schedule_end = #{schedule_end} 

	, schedule_memo= #{schedule_memo} 

where 
	schedule_num = #{schedule_num}
